(ns webmine.feeds-test
  (:require [clj-time.core :as time])
  (:use clojure.test
        webmine.feeds
        webmine.urls))

(deftest is-link-an-rss-feed
  (is (not (good-rss? "http://www.foo.com" "")))
  (is (not (good-rss?
	    "http://www.foo.com/xmlrpc.php" "application+rss")))
  (is (good-rss? "http://www.foo.com/feed.xml"  "application+rss"))
  (is (not (good-rss? "http://www.foo.com/feed.html" ""))))

(deftest find-rss-feeds
  (is (= (set ["http://www.huffingtonpost.com/feeds/original_posts/index.xml"
               "http://feeds.huffingtonpost.com/FeaturedPosts"
               "http://feeds.huffingtonpost.com/huffingtonpost/raw_feed"
               "http://feeds.huffingtonpost.com/huffingtonpost/TheBlog"
               "http://feeds.huffingtonpost.com/huffingtonpost/LatestNews"
               "http://www.huffingtonpost.com/wires/full_index.rdf"])
         (set (host-rss-feeds (url "http://www.huffingtonpost.com"))))))

(deftest canonical-rss-feed
  (is (= "http://feeds.huffingtonpost.com/FeaturedPosts"
	 (canonical-feed (url "http://www.huffingtonpost.com"))))
  (is (= "http://feeds.huffingtonpost.com/FeaturedPosts"
	 (canonical-feed (url "http://www.huffingtonpost.com"))))
  (is (= "http://feedproxy.google.com/TechCrunch"
	 (canonical-feed "http://techcrunch.com/2010/11/20/no-palin-no-galin/")))
  (is (= nil
	 (canonical-feed "http://friendfeed.com/paul/6c012ca5/europe-web-of-debt")))
  (is (= "http://feeds.feedburner.com/ommalik"
	 (canonical-feed "http://gigaom.com")))
  (is (= nil
	 (canonical-feed "http://friendfeed.com/paul/74dd00cc/is-anyone-energized-by-doing-email")))
  (is (= "http://www.npr.org/rss/rss.php?id=1009"
	 (canonical-feed "http://www.npr.org/2010/11/19/131450204/discontent-swells-in-hosni-mubarak-s-egypt")))
  (is (= "http://www.nytimes.com/rss2"
	 (canonical-feed "http://www.nytimes.com/2011/01/19/nyregion/19divincenzo.html?_r=1&src=twt&twt=nytimes")))
  (is (= "http://www.rollingstone.com/siteServices/rss/allNews"
	 (canonical-feed "http://www.rollingstone.com/")))
  ;;should be /rss/rss.php?id=1001
  (is (= "http://npr.org/rss/rss.php?id=3"
	 (canonical-feed "http://npr.org")))
  (is (= "http://io9.com/index.xml"
	 (canonical-feed "http://io9.com"))))

(def good-tc
{:text "<p><img src=\"http://tctechcrunch.files.wordpress.com/2010/11/dashboard.jpg\" />Computer power management software company, <a href=\"http://www.1e.com/index.aspx\">1E</a> has released a new version of its marquee product, <a href=\"http://www.1e.com/softwareproducts/nightwatchman/index.aspx\">NightWatchman</a>.</p> <p>Like its predecessors, version 6.0, helps corporations manage their network of computers to optimize energy efficiency. It gives IT managers the ability to remotely power down computers and establish energy-saving settings (ie. automatic shutdown of desktops during the weekend).</p> <p>In the latest version, <a href=\"http://www.crunchbase.com/company/1e\">1E</a> has added three key features: a new web-based dashboard (to help managers oversee the entire company&#8217;s computer power usage), improved diagnostic tools to determine why a computer hasn&#8217;t properly powered down, and tariff calculations based on location.</p> <p>Given how the price of energy can fluctuate significantly on a region by region basis, the new location-based calculations will help companies more accurately assess how much they&#8217;re saving on energy usage. Energy efficiency as it relates to IT management is becoming an increasingly important field, and according to Gartner Research, in two years more than half of mid and large-sized businesses will centrally manage their desktops&#8217; energy consumption.</p> <p>Although the average NightWatchman PC only saves $36 a year in energy costs, those incremental savings yield significant sums in aggregate. Several of 1E&#8217;s clients are large corporations with massive IT operations, such as AT&amp;T, Ford and Dell. According to 1E, NightWatchman has 4.6 million licensed users around the world, a group that has collectively saved $530 million in energy costs.</p> <div class=\"cbw snap_nopreview\"><div class=\"cbw_header\"><script src=\"http://www.crunchbase.com/javascripts/widget.js\" type=\"text/javascript\"></scr